var current_environment = document.location.hostname;
function track_user_activity(t, e, c = "", i = "") {
  if ("arbitragecalc.com" == current_environment) {
    var a = location.href;
    location.host.split(".");
    $.ajax({
      url: "Track_user_activity/track_user",
      type: "GET",
      cache: !1,
      data: {
        tracker_id: "arbitragecalc",
        app_id: "web",
        action: t,
        page: e,
        current_url: a,
        event_name: c,
        event_value: i,
      },
      success: function (t) {
        console.log(t);
      },
    });
  }
}
$(window).load(function () {
  $(window).width() <= 767 &&
    $(".quick-links h2.title").click(function () {
      $(".quick-links ul").slideToggle(""),
        $(".quick-title").toggleClass("active"),
        setTimeout(function () {
          $("#quick-scroll").perfectScrollbar("update");
        }, 500);
    }),
    track_user_activity("page_load", "home");
});
